import { TodoCount } from './todosComp/TodoCount';
import { CreateTodoButton } from './todosComp/CreateTodosButton/index';
import { TodoSearch } from './todosComp/TodoSearch';
import { TodoList } from './todosComp/TodoList';
import { TodoItem } from './todosComp/TodoItem';
import './css/styleGeneral.css';
import React from 'react';
import './App.css';
// import { jsx } from 'react/jsx-runtime';
// const defaultTodos = [
//   { text: 'Tarea 1', completed: true, swapy: 'item1' },
//   { text: 'tarea 2', completed: false , swapy: 'item2'},
//   { text: 'tarea 3', completed: true , swapy: 'item3'},
//   { text: 'tarea 4', completed: false, swapy: 'item4' },
// ];

// const defaultTodos= [];
function useLocalStorage(itemName,initialValue)
{
  const localStorageItem = localStorage.getItem(itemName);
  let parsedItem;

  if(!localStorageItem)
  {
    localStorage.setItem(itemName,JSON.stringify(initialValue));
    parsedItem = initialValue;
  }
  else
  {
    parsedItem = JSON.parse(localStorageItem);
  }
  const [item,setItem] = React.useState(parsedItem);
  const saveItem = (newItem) =>{
    localStorage.setItem(itemName,JSON.stringify(parsedItem));
    setItem(newItem);
  };

  return [item,saveItem];
}

function App() {

  const [todos,saveTodos] = useLocalStorage('TODOS_V1',[]);
  const [searchValue, setSearchValue] = React.useState('');



  const todosCompleted = todos.filter(todo => todo.completed ).length;
  const totalTodos = todos.length;
  const searchTodos = todos.filter(todo => {
    return todo.text.toLocaleLowerCase().includes(searchValue.toLocaleLowerCase())
  });


  const deleteTodos = (text) =>{
    const newTodos = [...todos];
    const todoIndex = newTodos.findIndex(
      (todo) => todo.text === text
    );
    newTodos.splice(todoIndex, 1);
    saveTodos(newTodos);
  };
  const completeTodos = (text) =>{
    const newTodos = [...todos];
    const todoIndex = newTodos.findIndex(
      (todo) => todo.text === text
    );

    newTodos[todoIndex].completed = true;
    saveTodos(newTodos);
  };
  //JSX
  return (
    <div className='card_principal'>
      <TodoCount
        completed={todosCompleted}
        total={totalTodos} />
      <TodoSearch 
        searchValue={searchValue}
        setSearchValue={setSearchValue}
      />
      <TodoList>
        {searchTodos.map(todo =>(
        <TodoItem 
          key={todo.text}
          text={todo.text}  
          completed={todo.completed} 
          onComplete={()=>completeTodos(todo.text)}
          onDelete={()=>deleteTodos(todo.text)}
          />
        ))}
      </TodoList>

      <CreateTodoButton />
    </div>
  );
}




export default App;
