import { TodoCount } from '../todosComp/TodoCount/index';
import { CreateTodoButton } from '../todosComp/CreateTodosButton/index';
import { TodoSearch } from '../todosComp/TodoSearch/index';
import { TodoList } from '../todosComp/TodoList/index';
import { TodoItem } from '../todosComp/TodoItem/index';
import React from 'react';

function AppUI({
    todosCompleted,
    totalTodos,
    searchValue,
    setSearchValue,
    searchTodos,
    completeTodos,
    deleteTodo,
    loading,
    error
}){
    return(
        <div className='card_principal'>
            <TodoCount
            completed={todosCompleted}
            total={totalTodos} />
            <TodoSearch 
            searchValue={searchValue}
            setSearchValue={setSearchValue}
            />
            {loading && <p>Estado de espera</p>}
                {error && <p>algo salio mal</p>}
                {(!loading && searchTodos.length === 0) && <p>Ingresa un todo</p>}
            <TodoList>
            {searchTodos.map(todo =>(
            <TodoItem 
                
                key={todo.text}
                text={todo.text}  
                completed={todo.completed} 
                onComplete={()=>completeTodos(todo.text)}
                onDelete={()=>deleteTodo(todo.text)}
                />
            ))}
            </TodoList>

            <CreateTodoButton />
        </div>
    );
    
}
export {AppUI};
