import { TodoCount } from './todosComp/TodoCount';
import { CreateTodoButton } from './todosComp/CreateTodosButton';
import { TodoSearch } from './todosComp/TodoSearch';
import { TodoList } from './todosComp/TodoList';
import { TodoItem } from './todosComp/TodoItem';
import './css/styleGeneral.css';
import React from 'react';
import './App.css';
import { jsx } from 'react/jsx-runtime';
// const defaultTodos = [
//   { text: 'Tarea 1', completed: true, swapy: 'item1' },
//   { text: 'tarea 2', completed: false , swapy: 'item2'},
//   { text: 'tarea 3', completed: true , swapy: 'item3'},
//   { text: 'tarea 4', completed: false, swapy: 'item4' },
// ];
function App() {




  const localStorageTodos = localStorage.getItem('TODOS_V1');
  let parsedTodos;

  if(!localStorageTodos)
  {
    localStorage.setItem('TODOS_V1',JSON.stringify(defaultTodos));
    parsedTodos = [];
  }
  else
  {
    parsedTodos = JSON.parse(localStorageTodos);
  }
  const [searcValue, setSearchValue] = React.useState('');
  const [todos,setTodos] = React.useState(parsedTodos);

  // const [todos, setTodos] = React.useState(() => {
  //   const todosFromStorage = window.localStorage.getItem('TODOS_V1')
  //   if (todosFromStorage) return JSON.parse(todosFromStorage)
  //   return []
  // });

  const todosCompleted = todos.filter(todo => todo.completed ).length;
  const totalTodos = todos.length;
  const searchTodos = todos.filter(todo => {
    return todo.text.toLocaleLowerCase().includes(searcValue.toLocaleLowerCase())
  });
const saveTodos = (arrayTodos) =>{
  localStorage.setItem('TODOS_V1',JSON.stringify(arrayTodos));
  setTodos(arrayTodos);
};

  const deleteTodos = (text) =>{
    const newTodos = [...todos];
    const todoIndex = newTodos.findIndex(
      (todo) => todo.text == text
    );
    newTodos.splice(todoIndex, 1);
    saveTodos(newTodos);
  };
  const completeTodos = (text) =>{
    const newTodos = [...todos];
    const todoIndex = newTodos.findIndex(
      (todo) => todo.text == text
    );

    newTodos[todoIndex].completed = true;
    saveTodos(newTodos);
  };
  //JSX
  return (
    <div className='card_principal'>
      <TodoCount
        completed={todosCompleted}
        total={totalTodos} />
      <TodoSearch 
        searcValue={searcValue}
        setSearchValue={setSearchValue}
      />
      <TodoList>
        {searchTodos.map(todo =>(
        <TodoItem 
          key={todo.text}
          text={todo.text}  
          completed={todo.completed} 
          onComplete={()=>completeTodos(todo.text)}
          onDelete={()=>deleteTodos(todo.text)}
          />
        ))}
      </TodoList>

      <CreateTodoButton />
    </div>
  );
}




export default App;
